import React, { useRef } from "react";
import classes from "./MealForm.module.css";
import Input from "../UI/Input";

const MealForm = () => {
  const ref = useRef();
  return (
    <form className={classes.form}>
      <Input
        label="Amount"
        ref={ref}
        input={{
          type: "number",
          min: "1",
          max: "8",
          step: "1",
          defaultValue: "1",
        }}
      />
      <button>+ Add</button>
    </form>
  );
};

export default MealForm;
