import React from "react";
import ContextObj from "./ContextObj";

const ContextProvider = (props) => {
  const addItem = () => {};
  const removeItem = () => {};
  const store = {
    items: [],
    totalAmout: 0,
    addItem,
    removeItem,
  };
  return (
    <ContextObj.Provider value={store}>{props.children}</ContextObj.Provider>
  );
};

export default ContextProvider;
